import React, { useEffect, useState } from "react";
import { Carousel } from "react-bootstrap";
import { collecting } from "./Bridge";

export const Past=()=>{
    const[every,setEvery]=useState([])

    useEffect(()=>{
        gather()
    },[])

    const gather=async()=>{
        const t = await collecting()
        setEvery(t.data.filter((data)=>{
            return data.winner !== null
        }))
    }
    return(<>
        <div className="container mt-lg-2">
            <div className="row justify-content-center">
                {every.map((data)=>(
                    <Carousel className="col-lg-8 col-md-10 col-sm-12 text-center">
                        <Carousel.Item>
                            <h1 className="display-1">{data.name}</h1>
                            <Carousel.Caption>
                            <h3>{data.tournamentId}</h3>
                            <h3>{data.venue}</h3>
                            <h3>{data.startDate}</h3>
                            <h1>{data.Winner}</h1>
                            <button className="btn btn-outl">

                            </button>
                            </Carousel.Caption>
                        </Carousel.Item>
                    </Carousel>
                ))}
            </div>
        </div>
    </>);
}
