import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { collecting } from "./Bridge";

export const Participants=()=>{
    const{position}=useParams()

    const[tour,setTour]=useState({
        "tournamentId":0,
        "name":"",
        "participants":[],
        "startDate":"",
        "winner":"",
        "venue":"",
        "price":0
    })

    useEffect(()=>{
        gather()
    },[])

    const gather=async()=>{
        const t=await collecting()
        setTour(t.data[position])
    }
    return(<>
        <p className="text-center mt-lg-4 text-primary display-4">
            Athelets of {tour.name} Tournament
        </p>
        <div className="container">
            <div className="row justify-content-center p-3">
                {tour.participants.map((data,index)=>(
                    <div className="card col-lg-3 col-md-4 col-sm-6 text-primary bg-danger p-4">
                        <h1 className="text-center card-title">{data.name}</h1>
                        <hr></hr>
                        <div className="card-body">

                        </div>
                    </div>
                ))}
            </div>
        </div>
    </>);
}
