import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { collecting } from "./Bridge";

export const Athlete=()=>{
    const{position}=useParams()
    const[tour,setTour]=useState({
        "tournamenetId":0,
        "name":"",
        "startDate":"",
        "venue":"",
        "price":"",
        "winner":"",
        "participants":[]
    })

    const[athlete,setAthlete]=useState({
        "name":"",
        "contactNo":0,
        "weight":0.0,
        "tournament":tour
    })

    useEffect(()=>{
        gather()
    },[])

    const gather=async()=>{
        const t = await collecting()
        setTour(t.data[position])
    }

    const prepare=(eve)=>{
        const{name,value}=eve.target
        setAthlete((old)=>{
            return{
                ...old,
                [name]:value
            }
        })
    }

    
    return(
        <>
            <div className="row justify-content-center mt-lg-5">
                <div className="col-lg-8 col-md-10 col-sm-12 shadow p-5">
                    <h1 className="display-3 text-info text-center">New Tournament Schedule</h1>
                    <TextField
                        id="input-with-icon-textfield"
                        label="Athlete name"
                        InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                            <AccountCircle />
                            </InputAdornment>
                        ),
                        }}
                        variant="standard"
                        className="form-control"
                        name="name" 
                        value={athlete.name}
                        onChange={prepare}
                    />
                    <TextField id="outlined-basic" 
                        label="Contact Number" 
                        variant="outlined" 
                        className="form-control mt-2"
                        name="contactNo"
                        value={athlete.contactNo}
                        onChange={prepare}
                    />
                    <InputLabel htmlFor="outlined-adornment-">Price</InputLabel>
                    <OutlinedInput
                        id="outlined-adornment-prize"
                        name="price"
                        value={tournament.price}
                        onChange={gather}
                        placeholder="Prize amount for winner"
                        className="form-control mt-2"
                        startAdornment={<InputAdornment position="start">Rs.</InputAdornment>}
                        label="Price"
                    />
                </div>
            </div>
        </>
    );
}
