import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { collecting, letGet } from "./Bridge";

export const Athlets=()=>{
    const{position}=useParams()
    const [tournament,setTournament]=useState({
        "tournamentId":0,
        "name":"",
        "startDate":"",
        "venue":"",
        "price":0,
        "participants":[]
    })
    useEffect(()=>{
        gather()
    },[])

    const gather=async()=>{
        const t = await collecting()
        setTournament(t.data[position])
    }

    return(
        <>
            <p>{tournament.name}</p>
            <div className="container mt-lg-5">
                <div className="row justify-content-center">
                    <div className="table-responsive-md">
                        <table className="table table-bordered table-hover shadow">
                            <thead className="bg-info text-danger">
                                <tr>
                                    <th>Athlete Id</th><th>Contact</th>
                                    <th>Weight</th>
                                    <th>Name</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody className="bg-danger text-info">
                                {
                                    tournament.participants.map((data,index)=>(
                                        <tr>
                                            <td>{data.enrollId}</td>
                                            <td>{data.contactNo}</td>
                                            <td>{data.name}</td>
                                            <td>
                                            <a className="btn btn-outline-warning"
                                                href={`/ath/${index}`}>
                                                <AccountBoxOutlinedIcon/>
                                            </a>
                                        </td>
                                        </tr>
                                    ))
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </>
    );
}
