import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { collecting } from "./Bridge";

export const Athlete=()=>{
    const{position}=useParams()
    const[tour,setTour]=useState({
        "tournamenetId":0,
        "name":"",
        "startDate":"",
        "venue":"",
        "price":"",
        "winner":"",
        "participants":[]
    })

    const[athlete,setAthlete]=useState({
        "name":"",
        "contactNo":0,
        "weight":0.0,
        "tournament":{}
    })

    useEffect(()=>{
        gather()
    },[])

    const gather=async()=>{
        const t = await collecting()
        setTour(t.data[position])
    }

    
    return(
        <>
            <div className="row justify-content-center mt-lg-5">
                <div className="col-lg-8 col-md-10 col-sm-12 shadow p-5">
                    <h1 className="display-3 text-info text-center">New Tournament Schedule</h1>
                    <TextField
                        id="input-with-icon-textfield"
                        label="Athlete name"
                        InputProps={{
                        startAdornment: (
                            <InputAdornment position="start">
                            <AccountCircle />
                            </InputAdornment>
                        ),
                        }}
                        variant="standard"
                        className="form-control"
                        name="name" 
                        value={tournament.name}
                        onChange={gather}
                    />
                </div>
            </div>
        </>
    );
}
